START_TIME=$SECONDS

OPTIND=1         # ReSET p_status='G',in case getopts has been used previously in the shell.
# geocorrection_type N (needs geolocation) is fed from cronjob every 10 minutes
# geocorrection_type U (unknown geolocation) is fed from cronjob (once a night to check if there are new applicable rules)
geocorrection_type=""
while getopts "nu" opt; do
    case "$opt" in
    n)  geocorrection_type="p_status='N'"
        ;;
    u)  geocorrection_type="p_status='U'"
        ;;
    esac
done

